mapper.select:
        SELECT 

        FROM config_house h left join payment_contract p on h.id = p.resource_id AND p.enabled_mark = 1
        WHERE h.enabled_mark = 1 

				AND h.name = #{name}


				AND h.state = #{state}


				AND p.user_phone= #{userPhone}


				AND p.user_name = #{userName}

		LIMIT #{pageSize} OFFSET #{offset}

        SELECT 
        count(1)
         FROM config_house h left join payment_contract p on h.id = p.resource_id AND p.enabled_mark = 1
        WHERE h.enabled_mark = 1 

				AND h.name = #{name}


				AND h.state = #{state}


				AND p.user_phone= #{userPhone}


				AND p.user_name = #{userName}
