mapper.select:
        SELECT 

        FROM payment_contract_fee f 
        left join payment_contract p on f.contract_id = p.id
        left join config_house h on p.resource_id = h.id
        WHERE 1 = 1 

			AND f.fee_item_id = #{feeItemId}


			AND p.resource_name = #{resourceName}

		AND f.next_bill_date between #{beginDate} and #{endDate}
		ORDER BY next_bill_date asc

SELECT
FROM payment_contract_fee f left join payment_contract p on f.contract_id = p.id left join config_house h on p.resource_id = h.id WHERE 1 = 1
AND f.fee_item_id = #{feeItemId}
